import React from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';

function RedirectPage() {
    const navigate = useNavigate();

    React.useEffect(() => {
        setTimeout(() => {
            navigate('/target', { state: { showNotification: true } });
        }, 500);
    }, [navigate]);

    return <div>Redirecting you...</div>;
}

function TargetPage() {
    const location = useLocation();

    React.useEffect(() => {
        // Check if we need to show notification
        if (location.state && location.state.showNotification) {
            // Request permission if needed
            if (Notification.permission === 'granted') {
                showNotification();
            } else if (Notification.permission !== 'denied') {
                Notification.requestPermission().then(permission => {
                    if (permission === 'granted') {
                        showNotification();
                    }
                });
            }
            // Clear state so it doesn't show on refresh
            window.history.replaceState({}, document.title);
        }
    }, [location.state]);

    const showNotification = () => {
        new Notification('Donation Portal', {
            body: 'Our donation portal is still in progress, please send a message in the connect form in /about',
            icon: 'https://cdn-icons-png.flaticon.com/512/2917/2917994.png', // Optional icon URL
        });
    };

    return (
        <div>
            <h1>Welcome to the Target Page</h1>
            <p>Check your browser notifications!</p>
        </div>
    );
}

export default function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<RedirectPage />} />
                <Route path="/target" element={<TargetPage />} />
            </Routes>
        </Router>
    );
}
